mapper.update:
UPDATE QUESTION_ANSWER
   SET TITLE = #{title}
	 , MEMO = #{memo}
 WHERE QST_SEQ = #{qstSeq}
   AND CUST_ID = #{custId}

UPDATE QUESTION_ANSWER
   SET USER_ID = #{userId}
	 , CHRG_DATE = TO_CHAR(SYSDATE,'yyyyMMddHH24MISS')
 WHERE QST_SEQ = #{qstSeq}
   AND CUST_ID = #{custId}
   AND (USER_ID IS NULL OR USER_ID = '')

UPDATE QUESTION_ANSWER
   SET ANSWER = #{answer}
	 , ANSWER_DATE = TO_CHAR(SYSDATE,'yyyyMMddHH24MISS')
 WHERE QST_SEQ = #{qstSeq}
   AND CUST_ID = #{custId}
   AND USER_ID = #{userId}

mapper.delete:
DELETE FROM QUESTION_ANSWER
 WHERE QST_SEQ = #{qstSeq}
   AND CUST_ID = #{custId}
