mapper.insert:
INSERT INTO USER
(
  USER_ID
, USER_NAME
, PWD
, PWD_HINT
, PWD_ANSWER
, TRY_CNT
, RGST_DATE
)
VALUES
(
  #{userId}
, #{userName}
, #{pwd}
, #{pwdHint}
, #{pwdAnswer}
, 0
, TO_CHAR(SYSDATE,'yyyyMMddHH24MISS')
)

mapper.update:
UPDATE USER
   SET TRY_CNT = TRY_CNT + 1
 WHERE USER_ID = #{userId}

UPDATE USER
   SET TRY_CNT = 0
 WHERE USER_ID = #{userId}
  AND PWD = #{pwd}
